insert into question (idquestion,idniveau,question,valeur) values ('1','1','Adherence au systeme d information : Comment jugez-vous l importance de votre systeme d information dans l accomplissement de vos missions ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('2','1','Niveau des impacts internes : Quelles sont les consequences internes (perturbations du fonctionnement, impacts financiers, impacts juridiques…) d un sinistre touchant la securite de votre systeme d information (ex : deni de service, perte d informations, attaque virale…) ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('3','1','Niveau des impacts externes : Quelles sont les consequences externes (image, securite de l environnement, relations contractuelles, securite des personnes…) d un sinistre touchant la securite de votre systeme d information ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('4','2','Besoins de disponibilite (propriete d accessibilite au moment voulu par des personnes autorisees) : Dans quelle mesure la disponibilite du(des) systeme(s) informatique(s) est-elle importante ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('5','2','Besoins d integrite (propriete d exactitude et de completude) : Dans quelle mesure l integrite des donnees manipulees ou manipulables dans le cadre de l activite est-elle importante ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('6','2','Besoins de confidentialite (propriete de n être accessible qu aux personnes autorisees) : Dans quelle mesure la confidentialite des informations exploitees ou exploitables dans le cadre de l activite est-elle importante ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('7','3','Frequence des sinistres SSI : Quelle est la frequence estimee des sinistres SSI dans l organisme ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('8','3','Degre de motivation des attaquants : Quel est le degre de motivation des attaquants potentiels ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('9','3','Moyens des attaquants : Quels sont les competences et les ressources des attaquants potentiels ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('10','4','Heterogeneite du systeme d information : Quel est le niveau d heterogeneite du systeme d information ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('11','4','Ouverture du systeme d information : Quel est le degre d ouverture du systeme d information ?','0');
insert into question (idquestion,idniveau,question,valeur) values ('12','4','Variabilite du systeme d information : Quel est le niveau de variabilite des composants du systeme d information (materiels, logiciels, reseaux, organisations, locaux, personnel…) et du contexte dans lequel il opere (contraintes, exigences reglementaires, menaces…) ?','0');
